import { useState } from 'react';

const hay = Math.floor(Math.random() * 9);  // Randomize the hay square

function Square({ value, onInputChange }) {
  let myValue;
  if (value === null) {
    myValue = 'üåæ';
  } else if (value === 'X') {
    myValue = '‚ùå';
  } else {
    myValue = 'üìå';
  }

  return (
    <div>
      <input
        className="square"
        type="text"
        value={myValue}
        onChange={(e) => onInputChange(e.target.value)}
        required
        minLength="4"
        maxLength="8"
        size="10"
      />
    </div>
  );
}

function App() {
  const [squares, setSquares] = useState(Array(25).fill(null));

  function handleClick(i) {
    const nextSquares = squares.slice();
    if (i === hay) {
      nextSquares[i] = "H";  // 'H' for hay
    } else {
      nextSquares[i] = "X";  // 'X' for a wrong square
    }
    setSquares(nextSquares);
  }

  function handleInputChange(value, i) {
    const nextSquares = squares.slice();
    nextSquares[i] = value;  // Update the value of the clicked square
    setSquares(nextSquares);
  }

  return (
    <>
      {[...Array(5)].map((_, rowIndex) => (
        <div className="board-row" key={rowIndex}>
          {[...Array(5)].map((_, colIndex) => {
            const i = rowIndex * 5 + colIndex;  // Calculate index for each square
            return (
              <Square
                key={i}
                value={squares[i]}
                onInputChange={(value) => handleInputChange(value, i)}
              />
            );
          })}
        </div>
      ))}
    </>
  );
}

export default App;
