import { useEffect, useState } from "react";
import { WordReader } from "./WordReader";

function Submit({ onSubmitClick }) {
  return (
    <div>
      <button className="square" onClick={onSubmitClick}>
        Submit
      </button>
    </div>
  );
}

function Square({ value, onInputChange, disabled, bgColor }) {
  return (
    <div>
      <input
        className="square"
        type="text"
        value={value || ""}
        onChange={(e) => onInputChange(e.target.value)}
        required
        minLength="4"
        maxLength="8"
        size="10"
        disabled={disabled} // Disable input if not in active row
      />
    </div>
  );
}

function App() {
  const [rows, setRows] = useState(Array(5).fill().map(() => Array(5).fill("")));
  const [activeRow, setActiveRow] = useState(0); // Track which row is editable
  const [line, setLine] = useState("");

  useEffect(() => {
    async function fetchLine() {
      const lineContent = await WordReader("/words.txt", 3); // Reads line 3
      setLine(lineContent);
    }

    fetchLine();
  }, []);

  function handleInputChange(value, i, j) {
    if (i !== activeRow) return; // Only allow changes in the current active row

    // Create a new copy of rows to update state correctly
    const newRows = rows.map((row, rowIndex) =>
      rowIndex === i ? row.map((col, colIndex) => (colIndex === j ? value : col)) : row
    );

    setRows(newRows);
  }

  function handleSubmitClick() {
    if (activeRow >= 5) return; // Stop if all rows are submitted

    const answer = line.trim();
    let num_correct = 0;

    for (let i = 0; i < 5; i++) {
      if (answer[i] === rows[activeRow][i]) {
        num_correct++;
      }
    }

    if (num_correct === 5) {
      alert("Congratulations! You got the correct answer! ðŸŽ‰");
      return;
    }

    // Move to the next row
    setActiveRow((prev) => prev + 1);
  }

  return (
    <>
      <div>
        {[...Array(5)].map((_, rowIndex) => (
          <div className="board-row" key={rowIndex}>
            {[...Array(5)].map((_, colIndex) => (
              <Square
                key={`${rowIndex}-${colIndex}`}
                value={rows[rowIndex][colIndex]}
                onInputChange={(value) => handleInputChange(value, rowIndex, colIndex)}
                disabled={rowIndex !== activeRow} // Disable past & future rows
              />
            ))}
          </div>
        ))}

        <Submit onSubmitClick={handleSubmitClick} />
      </div>
    </>
  );
}

export default App;
