import React, { useEffect, useState } from 'react';
import './timer.css';

function Timer({ isActive, onReset }) {
  const [time, setTime] = useState(0);

  useEffect(() => {
    let interval;
    if (isActive) {
      interval = setInterval(() => {
        setTime((prevTime) => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(interval);
    }

    return () => clearInterval(interval); // Cleanup on unmount or isActive change
  }, [isActive]);

  const handleReset = () => {
    onReset(); // Reset is handled by the parent
    setTime(0); // Reset the timer's time to 0
  };

  return (
    <div className="timer">
      <div>{time}s</div>
      <button onClick={handleReset} className="reset-timer-button">Reset Timer</button>
    </div>
  );
}

export default Timer;
