import { useState } from 'react';
import './minefield/style.css';
import Flag from './gamestate/Flag';
import Reset from './gamestate/Reset';
import Timer from './gamestate/Timer';
import Board from './minefield/Board';

function Game() {
  const [isActive, setIsActive] = useState(false);
  const [flag, setFlag] = useState(false);

  const handleFlagClicked = () => {
    setFlag((prevFlag) => !prevFlag); // Toggles the flag value
  };

  const handleReset = () => {
    setIsActive(false);
  };

  // Function to reset the game
  const handleGameReset = () => {
    setIsActive(false);  // Stops the timer
    setFlag(false);       // Resets the flag
    // Add any other game state resets here (e.g., resetting the board)
  };

  return (
    <div className="game">
      <Board isActive={isActive} setIsActive={setIsActive} flag={flag} setFlag={setFlag} />
      <div className="control-panel">
        <Flag flag={flag} onFlagClicked={handleFlagClicked} />
        <Reset onReset={handleGameReset} /> 
        <Timer isActive={isActive} onReset={handleReset} />
      </div>
    </div>
  );
}

export default Game;
