import {useState} from 'react';
import Square from './Square';

const hay = Math.floor(Math.random() * 9);

// initialises board
// -----------------
let board = Array();

for (let i = 0; i < 8; i++) {
    const row = Array();
    for (let j = 0; j < 8; j++) {
        row.push(null);
    }
    board.push(row);
}
console.log(board);
// -----------------

// adds random mines
// -----------------
const num_mines = 10;
for (let i = 0; i < num_mines; i++) {
    let m = Math.floor(Math.random() * 8);
    let n = Math.floor(Math.random() * 8);
    while (board[m][n] != null) {
      m = Math.floor(Math.random() * 8);
      n = Math.floor(Math.random() * 8);
    }
    board[m][n] = "X";
}
console.log(board);
// -----------------


